mapper.select:
SELECT t_cost_card.id as id, t_dish.name AS name, t_dish.`id` AS dishId, t_dish.assistant_code AS assistantCode, t_cost_card.cost_card_number AS costCardNumber, t_cost_card.main_cost AS mainCost, t_cost_card.assist_cost AS assistCost, t_cost_card.delicious_cost AS deliciousCost, t_cost_card.standard_cost AS standardCost FROM t_cost_card LEFT JOIN t_dish ON t_dish.id = t_cost_card.dish_id LEFT JOIN t_dish_tag ON t_dish.id = t_dish_tag.dish_id
( t_dish.name LIKE "%"#{searchDto.keyword}"%" OR t_dish.assistant_code LIKE "%"#{searchDto.keyword}"%" )
AND t_dish.tag_id IN
#{tagId}
AND t_dish.`status` != 3 AND t_dish.`category_id` != 6
LIMIT #{offset}, #{searchDto.pageSize}
