mapper.select:
SELECT storageItem.`id` AS id, storageItem.`name` AS name, storageItem.`item_number` AS itemNumber, storageItem.`assistant_code` AS assistantCode, storageItem.`tag_id` AS tagId, tag.`name` AS tagName, storageItem.`ingredient_id` AS ingredientId, ingredient.`name` AS ingredientName, storageItem.`supplier_party_id` AS supplierPartyId, supplier.`name` AS supplierName, storageItem.`order_unit_id` AS orderUnitId, storageItem.`storage_unit_id` AS storageUnitId, storageItem.`cost_card_unit_id` AS costCardUnitId, storageItem.`count_unit_id` AS countUnitId, storageItem.`order_to_storage_ratio` AS orderToStorageRatio, storageItem.`storage_to_cost_card_ratio` AS storageToCostCardRatio, storageItem.`max_storage_quantity` AS maxStorageQuantity, storageItem.`min_storage_quantity` AS minStorageQuantity, storageItem.`stock_out_type` AS stockOutType, storageItem.`total_stock_in_quantity` AS totalStockInQuantity, storageItem.`total_stock_in_money` AS totalStockInMoney, storageItem.`last_stock_in_price` AS lastStockInPrice, storageItem.`created_time` AS createdTime, storageItem.`last_modified_time` AS lastModifiedTime FROM `t_storage_item` storageItem LEFT JOIN `t_party_supplier` supplier ON storageItem.`supplier_party_id` = supplier.`party_id` LEFT JOIN `t_tag` tag ON storageItem.`tag_id` = tag.`id` LEFT JOIN `t_ingredient` ingredient ON ingredient.`id` = storageItem.`ingredient_id`
( storageItem.`name` LIKE "%"#{searchDto.keyword}"%" OR storageItem.`item_number` LIKE "%"#{searchDto.keyword}"%" OR storageItem.`assistant_code` LIKE "%"#{searchDto.keyword}"%" )
AND storageItem.`supplier_party_id` = #{searchDto.supplierPartyId}
AND storageItem.`tag_id` IN
#{tagId}
AND storageItem.`status` != 2
ORDER BY storageItem.`tag_id` ASC, storageItem.`id` ASC
LIMIT #{searchDto.offset}, #{searchDto.pageSize}

SELECT COUNT(storageItem.`id`) FROM `t_storage_item` storageItem
( storageItem.`name` LIKE "%"#{searchDto.keyword}"%" OR storageItem.`item_number` LIKE "%"#{searchDto.keyword}"%" OR storageItem.`assistant_code` LIKE "%"#{searchDto.keyword}"%" )
AND storageItem.`supplier_party_id` = #{searchDto.supplierPartyId}
AND storageItem.`tag_id` IN
#{tagId}
AND storageItem.`status` != 2
