mapper.select:
SELECT `id` AS id,
`serial_number` AS serialNumber,
`comment` AS comment,
`depot_id` AS depotId,
`handler_party_id` AS handlerPartyId,
`created_party_id` AS createdPartyId,
`money` AS money,
`status` AS status,
`type` AS type,
`created_time` AS createdTime,
`last_modified_time` AS lastModifiedTime
FROM `t_storage_report`


        `created_time` >= #{startTime}


        AND `created_time` <= #{endTime}


        AND `id` = #{report.id}


        AND `serial_number` LIKE '%${report.serialNumber}'


        AND `created_party_id` = #{report.createdPartyId}


        AND `depot_id` =#{report.depotId}


        AND `handler_party_id` = #{report.handlerPartyId}


ORDER BY `created_time` DESC
LIMIT #{offset}, #{pageSize}

SELECT `id` AS id, `serial_number` AS serialNumber, `comment` AS comment, `depot_id` AS depotId, `handler_party_id` AS handlerPartyId, `created_party_id` AS createdPartyId, `money` AS money, `status` AS status, `type` AS type, `created_time` AS createdTime, `last_modified_time` AS lastModifiedTime FROM t_storage_report
`depot_id` IN
#{depotId}
AND `created_time` >= #{startTime}
AND `created_time` <= #{endTime}
ORDER BY `created_time` DESC;
