mapper.select:
SELECT t_storage_item.`id`,t_storage_item.`name` AS itemName, SUM(t_storage_report_item.`quantity`) AS itemQuantity, SUM(t_storage_report_item.`price`*t_storage_report_item.`quantity`)AS itemMoney, t_party_supplier.`party_id` AS supplierpartyId, (SELECT t_party_employee.`name` FROM t_party_employee WHERE t_party_employee.party_id=t_storage_report.`handler_party_id`) AS handlerName, (SELECT t_party_employee.`name` FROM t_party_employee WHERE t_party_employee.party_id=t_storage_report.`created_party_id`) AS createdName FROM t_storage_item,t_storage_report,t_storage_report_item,t_party_supplier WHERE t_storage_item.`id` = t_storage_report_item.`item_id` AND t_storage_report_item.`report_id` = t_storage_report.`id` AND t_storage_report.`type`=1 AND t_party_supplier.`party_id` = t_storage_item.`supplier_party_id`
AND t_storage_item.`supplier_party_id`=#{supplierId}
AND t_storage_report.`created_time`>=#{startDate}
AND t_storage_report.`created_time`<=#{endDate}
GROUP BY t_storage_item.`id`

SELECT DISTINCT t_storage_item.* FROM t_storage_item
,t_storage_report,t_storage_report_item
t_storage_item.supplier_party_id = #{supplierPartyId}
AND t_storage_report.`id` = t_storage_report_item.`report_id` AND t_storage_report_item.`item_id`=t_storage_item.`id` AND t_storage_report.`depot_id` IN
#{depotId}
AND t_storage_item.`tag_id` IN
#{tagId}
AND (t_storage_item.`name` LIKE CONCAT('%',#{keyword},'%') OR t_storage_item.`assistant_code` LIKE CONCAT('%',#{keyword},'%') OR t_storage_item.`item_number` LIKE CONCAT('%',#{keyword},'%'))
LIMIT #{offset},#{pageSize}

SELECT COUNT(DISTINCT t_storage_item.id) FROM t_storage_item
,t_storage_report,t_storage_report_item
t_storage_item.supplier_party_id = #{supplierPartyId}
AND t_storage_report.`id` = t_storage_report_item.`report_id` AND t_storage_report_item.`item_id`=t_storage_item.`id` AND t_storage_report.`depot_id` IN
#{depotId}
AND t_storage_item.`tag_id` IN
#{tagId}
AND (t_storage_item.`name` LIKE CONCAT('%',#{keyword},'%') OR t_storage_item.`assistant_code` LIKE CONCAT('%',#{keyword},'%') OR t_storage_item.`item_number` LIKE CONCAT('%',#{keyword},'%'))
