SELECT `t_vip_card`.`id`, `t_vip_card`.`vip_party_id`, `t_vip_card`.`card_number`, `t_vip_card`.`physical_number`, `t_vip_card`.`validity_time`, `t_vip_card`.`permanently_effective`, `t_vip_card`.`operator_party_id`, `t_vip_card`.`status`, `t_vip_card`.`created_time`, `t_party_vip_info`.`name`, `t_party_vip_info`.`phone`, `t_party_employee`.`name` AS `operator` FROM `t_vip_card` LEFT JOIN `t_party_vip_info` ON `t_vip_card`.`vip_party_id` = `t_party_vip_info`.`party_id` LEFT JOIN `t_party_employee` ON `t_vip_card`.`operator_party_id` = `t_party_employee`.`party_id` WHERE `t_vip_card`.`status` != 3
AND (`t_party_vip_info`.`name` LIKE "%"#{keyword}"%" OR `t_party_vip_info`.`phone` LIKE "%"#{keyword}"%")
AND `t_vip_card`.`created_time` >= #{startTime}
AND `t_vip_card`.`created_time` <= #{endTime}
ORDER BY `id` ASC LIMIT #{offset}, #{pageSize}

SELECT COUNT(1) FROM `t_vip_card` LEFT JOIN `t_party_vip_info` ON `t_vip_card`.`vip_party_id` = `t_party_vip_info`.`party_id` LEFT JOIN `t_party_employee` ON `t_vip_card`.`operator_party_id` = `t_party_employee`.`party_id` WHERE `t_vip_card`.`status` != 3
AND (`t_party_vip_info`.`name` LIKE "%"#{keyword}"%" OR `t_party_vip_info`.`phone` LIKE "%"#{keyword}"%")
AND `t_vip_card`.`created_time` >= #{startTime} AND `t_vip_card`.`created_time` <= #{endTime}
