mapper.select:
SELECT tableVipDishPrice.`id` AS `id`, dish.`dish_number` AS dishNumber, dish.`assistant_code` AS assistantCode, tableVipDishPrice.`dish_id` AS dishId, dish.`name` AS dishName, dish.`price` AS price, dish.`sale_price` AS salePrice, tableVipDishPrice.`vip_dish_price` AS vipDishPrice FROM `t_vip_dish_price` tableVipDishPrice, `t_dish` dish
(dish.`dish_number` LIKE "%"#{keyword}"%" OR dish.`assistant_code` like "%"#{keyword}"%" OR dish.`name` like "%"#{keyword}"%") AND dish.`id` = tableVipDishPrice.`dish_id` AND tableVipDishPrice.vip_dish_price_plan_id = #{vipDishPricePlanId}
ORDER BY tableVipDishPrice.`id` ASC LIMIT #{offset},#{pageSize}
